import axios from 'axios'

export function PostData(type, data) {
    let BaseURL = 'http://localhost:8080/';
    //let BaseURL = 'http://localhost:8080//';

    return new Promise((resolve, reject) =>{
      
      axios.post(BaseURL+type,JSON.stringify(data))
          .then((response) => response.json())
          .then((res) => {
            resolve(res);
          })
          .catch((error) => {
            reject(error);
          });

  
      });
}
