import React, {Component} from 'react'
import {Redirect} from 'react-router-dom';
import {PostData} from '../../services/PostData';
import './Login.css'

class Login extends Component {

    constructor(){
      super();
     
      this.state = {
       mail: '',
       password: '',
       redirectToReferrer: false
      };
  
      this.login = this.login.bind(this);
      this.onChange = this.onChange.bind(this);
  
    }
  
    
  
    login() {
      if(this.state.mail && this.state.password){
        PostData('login',this.state).then((result) => {
         let responseJson = result;
         console.log(responseJson);
         /*
         if(responseJson.result){         
           localStorage.setItem('token',responseJson.token);
           this.setState({redirectToReferrer: true});
         }
         */
        });
      }
      
     }
  
    onChange(e){
      this.setState({[e.target.name]:e.target.value});
     }
  
    
    
  
    render() {
  /*
    if (this.state.redirectToReferrer) {
        return (<Redirect to={'/'}/>)
    }
     
    if(localStorage.getItem('token')){
        return (<Redirect to={'/'}/>)
    }*/
  
    return (
        <div className="container">
            <h3>Login</h3>
            <form>
                <div class="form-group">
                    <label for="mail">E-Mail:</label>
                    <input type="email" class="form-control" id="mail" name="mail" onChange={this.onChange}/>
                </div>
                <div class="form-group">
                    <label for="pwd">Password:</label>
                    <input type="password" class="form-control" id="pwd" name="password" onChange={this.onChange}/>
                </div>

                <input type="submit" className="btn btn-success" value="Login" onClick={this.login}/>

            </form>
        </div>
      );
    }
  }
  
  export default Login;
