import React, {Component} from 'react'
import axios from 'axios'
import {Redirect,Link} from 'react-router-dom';

class ToDoItem extends Component{

    constructor({match}){
        super();

        this.state = {
            lists: [],
            filter_items: [],
            name: '',
            description: '',
            deadline: '', 
            filter_name: '',
            status_id: '',
            list_id: match.params.id
        };

        this.deleteItem = this.deleteItem.bind(this);
        this.onChange = this.onChange.bind(this);
        this.markItem = this.markItem.bind(this);
        this.createItem = this.createItem.bind(this);
        this.filterItem = this.filterItem.bind(this);

    }
    componentWillMount(){
      axios.post("http://localhost:8080/todolist/todoitem/listoftodoitem/",{
        token: localStorage.getItem('token'),
        list_id: this.state.list_id
      }).
      then((response) => {
        if(response.data.result){
          document.getElementById("list_name").innerHTML = response.data.list_name;
          this.setState({
            lists: response.data.todoitems
          })
        }
      });
    }

    deleteItem(item_id){
      axios.post("http://localhost:8080/todolist/todoitem/deleteitem",{
        token: localStorage.getItem('token'),
        id: item_id
      }).then((response) => {
        if(response.data.result){
          window.location.reload();
        }
        else{
          alert(response.data.message);
        }
      })
    }

    markItem(item_id){
      
      axios.post("http://localhost:8080/todolist/todoitem/markcompleted",{
        token: localStorage.getItem('token'),
        todo_item_id: item_id
      }).then((response) => {
        if (response.data.result) {
          window.location.reload();   
      }
      else alert(response.data.message);
      });
    }

    // work
    createItem(){
      if(this.state.name && this.state.deadline){
        axios.post("http://localhost:8080/todolist/addtodoitem",{
          token: localStorage.getItem('token'),
          list_id: this.state.list_id,
          name: this.state.name,
          description: this.state.description,
          deadline: this.state.deadline
        }).then((response) => {
          window.location.reload();
        })
      }
      else{
        alert("We need the all item property!");
      }
    }

    // work
    onChange(e){
      this.setState({[e.target.name]:e.target.value});
    }

    filterItem(){
      axios.post('http://localhost:8080/todolist/todoitem/filteritem',{
        token: localStorage.getItem('token'),
        name: this.state.filter_name,
        status_id: this.state.status_id,
        list_id: this.state.list_id
      }).then((response) => {
        if(response.data.result){
          this.setState({
            filter_items: response.data.toDoItems
          });

          console.log(response.data)

          const listItems = this.state.filter_items.map((list) => {

            var tr = document.createElement("TR");

            // id
            var id = document.createElement("TD");
            var id_text = document.createTextNode(list.id);
            id.appendChild(id_text);
            tr.appendChild(id);

            // name
            var name = document.createElement("TD");
            var name_text = document.createTextNode(list.name);
            name.appendChild(name_text);
            tr.appendChild(name);

            // description
            var description = document.createElement("TD");
            var description_text = document.createTextNode(list.description);
            description.appendChild(description_text);
            tr.appendChild(description);

            // created_date
            var created_date = document.createElement("TD");
            var created_date_text = document.createTextNode(list.created_date);
            created_date.appendChild(created_date_text);
            tr.appendChild(created_date);

            // updated_date
            var updated_date = document.createElement("TD");
            var updated_date_text = document.createTextNode(list.updated_date);
            updated_date.appendChild(updated_date_text);
            tr.appendChild(updated_date);

            // deadline
            var deadline = document.createElement("TD");
            var deadline_text = document.createTextNode(list.deadline);
            deadline.appendChild(deadline_text);
            tr.appendChild(deadline);

            // status_id
            var status_id = document.createElement("TD");
            var status_id_text = document.createTextNode("Water");
            status_id.appendChild(status_id_text);
            tr.appendChild(status_id);

          });

          document.getElementById('table_items').innerHTML = listItems;
        }
      });
    }


    render(){

      let lists = this.state.lists.map((list) => {
        return(
          <tr>
            <td>{list.id}</td>
            <td>{list.name}</td>
            <td>{list.description}</td>
            <td>{list.created_date}</td>
            <td>{list.updated_date}</td>
            <td>{list.deadline}</td>
            <td>{list.status_id == 1 ?  <label style={{color: 'green'}}>Completed</label> : <label style={{color: 'red'}}>Not Completed</label>}</td>
            <td>
              <Link className="btn btn-primary" to={'/todolist/'+list.list_id + '/' + list.id}>
                Dependencies
              </Link>
              &nbsp;
              <button className="btn btn-success" onClick={() => this.markItem(list.id)}>
                <i class="far fa-check-square"></i>
              </button>
              &nbsp;
              <button className="btn btn-danger" onClick={() => this.deleteItem(list.id)}>
                <i className="fas fa-trash"></i>
              </button>
            </td>
          </tr>
        );
      });

      if(!localStorage.getItem('token')){
        return (<Redirect to={'/'}/>)
      }

      return(
        <div className="container-fluid">
          <h2 id="list_name"></h2>
          <label>Create new item.</label>
          <hr/>
          <div className="row">
            <div className="col-md-3 col-lg-3">
              <input type="text" id="name" name="name" onChange={this.onChange} className="form-control" placeholder="Name"/>
            </div>
            <div className="col-md-5 col-lg-5">
              <input type="text" id="description" name="description" onChange={this.onChange} className="form-control" placeholder="Description"/>
            </div>
            <div className="col-md-2 col-lg-2">
              <input type="date" id="deadline" name="deadline" onChange={this.onChange} className="form-control"/>
            </div>
            <div class="col-md-2 col-lg-2">
              <input type="button" className="btn btn-success" value="Add a list item" onClick={this.createItem}/>
            </div>
          </div>
          <hr/>
          <h3>Filter İtems</h3>
          <hr />
          <div className="row">
            <div className="col-md-7 col-lg-7">
              <input type="text" name="filter_name" onChange={this.onChange} className="form-control" placeholder="Name"/>
            </div>
            <div className="col-md-3 col-lg-3">
              <select className="form-control" name="status_id" onChange={this.onChange}>
                <option selected disabled>Select optinon</option>
                <option value="1">Completed</option>
                <option value="2">Not completed</option>
              </select>
            </div>
            <div class="col-md-2 col-lg-2">
              <input type="button" className="btn btn-success" value="Filter Item" onClick={this.filterItem}/>
            </div>
          </div>
          <hr/>
          <br/>
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>List Name</th>
                <th>Description</th>
                <th>Created Date</th>
                <th>Updated Date</th>
                <th>Deadline</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody id="table_items">
              {lists}
            </tbody>
          </table>
        </div>
      );
    }
}

export default ToDoItem;
