import React, {Component} from 'react'
import axios from 'axios'
import {Redirect} from 'react-router-dom';

class ToDoItem extends Component{

    constructor({match}){
        super();

        this.state = {
            lists: [],
            name: '',
            list_id: match.params.id
        };

        this.deleteItem = this.deleteItem.bind(this);
        this.showDependencies = this.showDependencies.bind(this);

    }
    componentWillMount(){

      axios.post("http://localhost:8080/todolist/todoitem/listoftodoitem/",{
        token: localStorage.getItem('token'),
        list_id: this.state.list_id
      }).then((response) => {
        if(response.data.result){
          document.getElementById("list_name").innerHTML = response.data.list_name;
          this.setState({
            lists: response.data.todoitems
          })
        }
      });
    }

    deleteItem(e){
      alert(e.target.id);
    }

    showDependencies(e){
      
      axios.post("http://localhost:8080/todolist/todoitem/showdependencies",{
        token: localStorage.getItem('token'),
        todo_item_id: e.target.id
      }).then((response) => {
        alert(response.data.message);
        window.location.reload();
      })
    }

    render(){

        let lists = this.state.lists.map((list) => {
        return(
          <tr>
            <td>{list.id}</td>
            <td>{list.name}</td>
            <td>{list.description}</td>
            <td>{list.created_date}</td>
            <td>{list.updated_date}</td>
            <td>
              <input type="button" className="btn btn-primary" id={list.id} value="Show" onClick={this.showDependencies} />
              &nbsp;&nbsp;&nbsp;
              <input type="button" className="btn btn-danger" id={list.id} value="Delete!" onClick={this.deleteItem}/>
            </td>
          </tr>
        );
      })

      if(!localStorage.getItem('token')){
        return (<Redirect to={'/'}/>)
      }

      return(
        <div className="container">
        <h2 id="list_name"></h2>
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>List Name</th>
                <th>Description</th>
                <th>Created Date</th>
                <th>Updated Date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {lists}
            </tbody>
          </table>
        </div>
      );
    }
}

export default ToDoItem;
