import React, {Component} from 'react'
import axios from 'axios'
import {Redirect} from 'react-router-dom';
import './ToDoList.css'

class ToDoList extends Component{

    constructor(){
        super();

        this.state = {
            lists: []
           };
    }

    componentWillMount(){

      axios.post("http://localhost:8080/todolist",localStorage.getItem('token')).then((response) => {
        
        if(response.data.result){
          this.setState({
            lists: lists
          })
        }
      })
    }


    render(){

      if(!localStorage.getItem('token')){
        return (<Redirect to={'/'}/>)
      }

      return(
          <div className="container">
            <table className="table">
              <thead>
                <tr>
                  <th>Firstname</th>
                  <th>Lastname</th>
                  <th>Email</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>John</td>
                  <td>Doe</td>
                  <td>john@example.com</td>
                </tr>
                <tr>
                  <td>Mary</td>
                  <td>Moe</td>
                  <td>mary@example.com</td>
                </tr>
                <tr>
                  <td>July</td>
                  <td>Dooley</td>
                  <td>july@example.com</td>
                </tr>
              </tbody>
            </table>
          </div>
      );
    }
}

export default ToDoList;
