import React, {Component} from 'react'
import axios from 'axios'
import {Redirect} from 'react-router-dom';

class ToDoItem extends Component{

    constructor({match}){
        super();

        this.state = {
            lists: [],
            name: '',
            list_id: match.params.id
        };

    }
    componentWillMount(){

      axios.post("http://localhost:8080/todolist/todoitem/listoftodoitem",{
        token: localStorage.getItem('token'),
        list_id: this.state.list_id
      }).then((response) => {
        if(response.data.result){
          this.setState({
            lists: response.data.lists
          })
        }
      });
    }
    render(){

      let lists = this.state.lists.map((list) => {
        return(
          <tr>
            <td>{list.id}</td>
            <td>{list.name}</td>
            <td>{list.created_date}</td>
            <td>{list.updated_date}</td>
            <td>
              <input type="button" className="btn btn-primary" id={list.id} value="List Detail" onClick={this.showListItem}/>
              &nbsp;&nbsp;&nbsp;
              <input type="button" className="btn btn-danger" id={list.id} value="Delete List!" onClick={this.deleteList}/>
            </td>
          </tr>
        );
      })

      if(!localStorage.getItem('token')){
        return (<Redirect to={'/'}/>)
      }

      return(
        <div className="container">
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>List Name</th>
                <th>Created Date</th>
                <th>Updated Date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {lists}
            </tbody>
          </table>
        </div>
      );
    }
}

export default ToDoItem;
