import React, { Component } from 'react'
import {NavLink} from 'react-router-dom'
import './Header.css'

class Header extends Component{

    constructor(){
        super();
    }

    render(){

        if(localStorage.getItem('token')){
            return(
                <ul class="topnav">
                    <li>
                        <NavLink to="/">Home</NavLink>
                    </li>
                    <li class="right">
                        <NavLink to="/todolist" >My Lists</NavLink>
                    </li>
                    <li class="right">
                        <NavLink to="/exit" >Exit</NavLink>
                    </li>
                </ul>
            );
        }

        return(
            <ul class="topnav">
                <li>
                    <NavLink to="/">Home</NavLink>
                </li>
                <li class="right">
                { localStorage.getItem('token') ? <NavLink to="/exit" >Exit</NavLink> : <NavLink to="/registration" >Registration</NavLink>}
                </li>
                <li class="right">
                { localStorage.getItem('token') ? <NavLink to="/todolist" >My Lists</NavLink> : <NavLink to="/login" >Login</NavLink>}
                </li>
            </ul>
        );
    }
}

export default Header;
