import React, { Component } from 'react'
import {NavLink} from 'react-router-dom'
import './Header.css'

class Header extends Component{

    constructor(){
        super();
    }

    render(){
        return(
            <ul class="topnav">
                <li>
                    <NavLink to="/" className="active">Home</NavLink>
                </li>
                <li class="right">
                { localStorage.getItem('token') ? <NavLink to="/todolist" className="active">My Lists</NavLink> : <NavLink to="/login" className="active">Login</NavLink>}
                </li>
                <li class="right">
                { localStorage.getItem('token') ? <NavLink to="/exit" className="active">Exit</NavLink> : <NavLink to="/registration" className="active">Registration</NavLink>}
                </li>
            </ul>
        );
    }
}

export default Header;
